source.buildSpec:
{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "cd pipeline",
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npm run cdk synth -- -o dist"
      ]
    }
  },
  "artifacts": {
    "base-directory": "pipeline/dist",
    "files": [
      "LambdaStack.template.json"
    ]
  }
}
